	final Time timeout = restConfiguration.getTimeout();

	LegacyRestHandlerAdapter<DispatcherGateway, StatusOverviewWithVersion, EmptyMessageParameters> clusterOverviewHandler = new LegacyRestHandlerAdapter<>(
		restAddressFuture,
