	try {
		for (;;) {
			try {
				RevObject o = q.next();
				if (o == null)
					break;

				if (tipToPack.containsKey(o))
					o.add(inCachedPack);

				if (have.contains(o))
					haveObjs.add(o);
				if (want.contains(o)) {
					o.add(include);
					wantObjs.add(o);
					if (o instanceof RevTag)
