				if (ignoreMissingUninteresting
						&& not.contains(e.getObjectId()))
					continue;
				throw e;
			}
		}
	} finally {
		q.release();
	}

	RevObject listName = null;
	RevObject o;

	while ((o = walker.next()) != null) {
		if (o.has(hasObjectList)) {
